sub _table { my @tableHead = (
new EBox::Types::Text ( 'fieldName' => 'name', 'printableName' => __('Name'), 'size' => '12', 'unique' => 1, 'editable' => 1 ), new EBox::Types::HasMany ( 'fieldName' => 'members', 'printableName' => __('Members'), 'foreignModel' => 'MemberTable', 'view' => '/ebox/Object/View/MemberTable', 'backView' => '/ebox/Object/View/MemberTable', 'size' => '1', )
);
my $dataTable = { 'tableName' => 'ObjectTable', 'printableTableName' => __('Objects'), 'automaticRemove' => 1, 'modelDomain' => 'Objects', 'defaultActions' => ['add', 'del', 'editField' ], 'tableDescription' => \@tableHead, 'class' => 'dataTable', 'help' => __('Objects'), 'printableRowName' => __('object'), };
return $dataTable; }

sub _table { my @tableHead = (
new EBox::Types::Text ( 'fieldName' => 'name', 'printableName' => __('Name'), 'size' => '12', 'unique' => 1, 'editable' => 1 ), new EBox::Types::IPAddr ( 'fieldName' => 'ipaddr', 'printableName' => __('IP Address'), 'editable' => 1, ), new EBox::Types::MACAddr ( 'fieldName' => 'macaddr', 'printableName' => __('MAC Address'), 'editable' => 1, 'optional' => 1 ),
);
my $dataTable = { 'tableName' => 'MemberTable', 'printableTableName' => __('Members'), 'automaticRemove' => 1, 'defaultActions' => ['add', 'del', 'editField' ], 'modelDomain' => 'Objects', 'tableDescription' => \@tableHead, 'class' => 'dataTable', 'help' => __('Objects'), 'printableRowName' => __('member'), };
return $dataTable; }

sub _table {
my @tableDesc = ( new EBox::Types::Text( fieldName => 'server', printableName => __('Jabber server name'), size => 12, editable => 1, ), new EBox::Types::Int( fieldName => 'port', printableName => __('Port'), size => 6, editable => 1, defaultValue => 5222, ), new EBox::Types::Text( fieldName => 'user', printableName => __('Jabber user name'), size => 12, editable => 1, ), new EBox::Types::Password( fieldName => 'password', printableName => __('User password'), size => 12, editable => 1, minLength => 4, maxLength => 25, ), new EBox::Types::Boolean( fieldName => 'subscribe', printableName => __('Subscribe'), editable => 1, ), new EBox::Types::Text( fieldName => 'adminJID', printableName => __('Administrator Jabber Identifier'), size => 12, editable => 1, ), );
my $dataForm = { tableName => 'JabberDispatcherForm', printableTableName => __('Configure Jabber dispatcher'), modelDomain => 'Events', tableDescription => \@tableDesc, class => 'dataForm', help => __('In order to configure the Jabber event dispatcher ' . 'is required to be registered at the chosen Jabber ' . 'server or check subscribe to do register. The administrator ' . 'identifier should follow the pattern: user@domain[/resource]'), };
return $dataForm;
}
