sub setService 
              {
              my ($self, $active) = @_;
              if ($active xor $self->service) {
              $self->set_bool('active', $active);
              }
              }

sub service 
              {
              my $self = shift;
              return $self->get_bool('active');
              }

sub setSynchronized # (synchronized)
              {
              my ($self, $synchronized) = @_;

